ALTER DATABASE plutoless_studi  DEFAULT CHARACTER SET utf8  DEFAULT COLLATE utf8_general_ci;  CREATE TABLE TYPE(ptid int NOT NULL AUTO_INCREMENT,title char(40) NOT NULL,CONSTRAINT pk_t_ptid PRIMARY KEY (ptid));CREATE TABLE USER(uid int NOT NULL AUTO_INCREMENT,username char(20) NOT NULL,passwd char(32) NOT NULL,firstname char(30),lastname char(30),email char(50),CONSTRAINT pk_uid PRIMARY KEY (uid));CREATE TABLE POST(pid int NOT NULL AUTO_INCREMENT,uid int NOT NULL,ptid int NOT NULL,title char(30),content char(140),tstamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,CONSTRAINT pk_post_pid PRIMARY KEY (pid),CONSTRAINT fk_post_uid FOREIGN KEY (uid) REFERENCES USER(uid) ON DELETE CASCADE ON UPDATE CASCADE,CONSTRAINT fk_post_ptid FOREIGN KEY (ptid) REFERENCES ptid(ptid) ON DELETE CASCADE ON UPDATE CASCADE);INSERT INTO `POST` (`content`, `title`) VALUES('Paolo Nutine', 'Sunny Side Up'),('Florence + The Machine', 'Lungs'),('Massive Attack', 'Heligoland'),('Andre Rieu', 'Forever Vienna'),('Sade', 'Soldier of Love'),('test', 'test'),('test', 'test'),('test', 'test'),('test', 'test'),('t', 't');