ALTER DATABASE blog  DEFAULT CHARACTER SET utf8  DEFAULT COLLATE utf8_general_ci;  DROP TABLE IF EXISTS Post;DROP TABLE IF EXISTS Type;DROP TABLE IF EXISTS User;CREATE TABLE Type(ptid int NOT NULL AUTO_INCREMENT,title char(40) NOT NULL,CONSTRAINT pk_t_ptid PRIMARY KEY (ptid));CREATE TABLE User(uid int NOT NULL AUTO_INCREMENT,username char(20) NOT NULL,passwd char(32) NOT NULL,firstname char(30),lastname char(30),email char(50),CONSTRAINT pk_uid PRIMARY KEY (uid));CREATE TABLE Post(pid int NOT NULL AUTO_INCREMENT,uid int NOT NULL,ptid int NOT NULL,title char(30),content char(140),ts TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,CONSTRAINT pk_post_pid PRIMARY KEY (pid),CONSTRAINT fk_post_uid FOREIGN KEY (uid) REFERENCES User(uid),CONSTRAINT fk_post_ptid FOREIGN KEY (ptid) REFERENCES Type(ptid));CREATE TABLE Message(msgid int NOT NULL AUTO_INCREMENT,content char(140),CONSTRAINT pk_msg_msgid PRIMARY KEY (msgid));INSERT INTO Type (title) VALUES ('Default');INSERT INTO User(username) VALUES ('DefaultUser');INSERT INTO `Post` (`content`, `title`, uid, ptid) VALUES('Paolo Nutine', 'Sunny Side Up', 1, 1),('Florence + The Machine', 'Lungs',1, 1),('Massive Attack', 'Heligoland',1, 1),('Andre Rieu', 'Forever Vienna',1, 1),('Sade', 'Soldier of Love',1, 1),('test', 'test',1, 1),('test', 'test',1, 1),('test', 'test',1, 1),('test', 'test',1,1);